<template>
	<!-- #ifdef APP-NVUE -->
	<cell>
		<!-- #endif -->
		<view :class="disabled ? 'uni-list-item--disabled' : ''" :hover-class="disabled || showSwitch ? '' : 'uni-list-item--hover'"
		 class="uni-list-item" @click="onClick" @longpress="onLongpress">
			<view class="uni-list-item__container" :class="{'uni-list-item--first':isFirstChild}">
				<view v-if="thumb" class="uni-list-item__icon">
					<image :src="thumb" class="uni-list-item__icon-img" />
				</view>
				<view v-if="icons" class="uni-list-item__icon">

					<view :class="['iconfont',genre==1?'exclass':'inclass',icons]"></view>
				</view>
				<view v-else-if="showExtraIcon" class="uni-list-item__icon">
					<uni-icons :color="extraIcon.color" :size="extraIcon.size" :type="extraIcon.type" class="uni-icon-wrapper" />
				</view>
				<view class="uni-list-item__content">
					<slot />
					<text class="uni-list-item__content-title">
						<text class="titletxt">{{ title }}</text>
						<text v-if="remarks" class="uni-list-item__content-note">[{{remarks}}]</text>
					</text>
					<!-- #ifdef  APP-PLUS || H5 -->
					<text v-if="note" class="uni-list-item__content-note">
						<text>{{`${note} `}}</text>
						<image @click.stop="onshowproof(picture)" v-show="picture" :src="picture" class="uni-list-item__icon-img pictureimg" />
					</text>
					<!-- #endif -->
					<!-- #ifdef MP-WEIXIN -->
					<view v-if="note" class="uni-list-item__content-note">
						<text>{{`${note} `}}</text>
						<image @click.stop="onshowproof(picture)" v-show="picture" :src="picture" class="uni-list-item__icon-img pictureimg" />
					</view>
					<!-- #endif -->

				</view>
				<view class="uni-list-item__content amout_datetime" v-if="amount || datetime">
					<text v-if="amount" class="uni-list-item__content-title"> {{`¥ ${amount}`}}</text>
					<text v-if="datetime" class="uni-list-item__content-note">{{ datetime }}</text>
				</view>
				<view class="uni-list-item__content " v-if="budget">
					<text class="uni-list-item__content-note item-budget">{{ budget }}</text>
				</view>
				<view v-if="showBadge || showArrow || showSwitch" class="uni-list-item__extra">
					<text v-if="rightText" class="uni-list-item__extra-text">{{rightText}}</text>
					<uni-badge v-if="showBadge" :type="badgeType" :text="badgeText" />
					<switch v-if="showSwitch" :disabled="disabled" :checked="switchChecked" @change="onSwitchChange" />
					<uni-icons v-if="showArrow" :size="20" class="uni-icon-wrapper" color="#bbb" type="arrowright" />
				</view>
			</view>
		</view>
		<!-- #ifdef APP-NVUE -->
	</cell>
	<!-- #endif -->
</template>

<script>
	import uniIcons from '../uni-icons/uni-icons.vue'
	import uniBadge from '../uni-badge/uni-badge.vue'

	/**
	 * ListItem 列表子组件
	 * @description 列表子组件
	 * @tutorial https://ext.dcloud.net.cn/plugin?id=24
	 * @property {String} title 标题
	 * @property {String} note 描述
	 * @property {String} thumb 左侧缩略图，若thumb有值，则不会显示扩展图标
	 * @property {String} badgeText 数字角标内容
	 * @property {String} badgeType 数字角标类型，参考[uni-icons](https://ext.dcloud.net.cn/plugin?id=21)
	 * @property {String} rightText 右侧文字内容
	 * @property {Boolean} disabled = [true|false]是否禁用
	 * @property {Boolean} showArrow = [true|false] 是否显示箭头图标
	 * @property {Boolean} showBadge = [true|false] 是否显示数字角标
	 * @property {Boolean} showSwitch = [true|false] 是否显示Switch
	 * @property {Boolean} switchChecked = [true|false] Switch是否被选中
	 * @property {Boolean} showExtraIcon = [true|false] 左侧是否显示扩展图标
	 * @property {Boolean} scrollY = [true|false] 允许纵向滚动，需要显式的设置其宽高
	 * @property {Object} extraIcon 扩展图标参数，格式为 {color: '#4cd964',size: '22',type: 'spinner'}
	 * @event {Function} click 点击 uniListItem 触发事件
	 * @event {Function} switchChange 点击切换 Switch 时触发
	 */
	export default {
		name: 'UniListItem',
		components: {
			uniIcons,
			uniBadge
		},
		props: {
			icons: {
				type: String,
				default: ''
			}, // 类型图标
			genre: {
				type: Number,
				default: 1
			}, // 收支类型
			title: {
				type: String,
				default: ''
			}, // 列表标题
			note: {
				type: String,
				default: ''
			}, // 列表描述
			picture: {
				type: String,
				default: ''
			}, //支收凭证图片
			amount: {
				type: [String, Number],
				default: ''
			}, //列表金额
			budget: {
				type: String,
				default: ''
			},
			remarks: {
				typ: String,
				default: ''
			}, //备注
			datetime: {
				type: String,
				default: ''
			}, //列表日期时间
			disabled: {
				// 是否禁用
				type: [Boolean, String],
				default: false
			},
			showArrow: {
				// 是否显示箭头
				type: [Boolean, String],
				default: true
			},
			showBadge: {
				// 是否显示数字角标
				type: [Boolean, String],
				default: false
			},
			showSwitch: {
				// 是否显示Switch
				type: [Boolean, String],
				default: false
			},
			switchChecked: {
				// Switch是否被选中
				type: [Boolean, String],
				default: false
			},
			badgeText: {
				// badge内容
				type: String,
				default: ''
			},
			badgeType: {
				// badge类型
				type: String,
				default: 'success'
			},
			rightText: {
				// 右侧文字内容
				type: String,
				default: ''
			},
			thumb: {
				// 缩略图
				type: String,
				default: ''
			},
			showExtraIcon: {
				// 是否显示扩展图标
				type: [Boolean, String],
				default: false
			},
			extraIcon: {
				type: Object,
				default () {
					return {
						type: 'contact',
						color: '#000000',
						size: 20
					}
				}
			}
		},
		inject: ['list'],
		data() {
			return {
				isFirstChild: false
			}
		},
		mounted() {
			if (!this.list.firstChildAppend) {
				this.list.firstChildAppend = true
				this.isFirstChild = true
			}
		},
		methods: {
			onClick() {
				this.$emit('click')
			},
			onLongpress() {
				this.$emit('longpress')
			},
			onSwitchChange(e) {
				this.$emit('switchChange', e.detail)
			},
			onshowproof(e) {
				this.$emit('onshowproof', e)
			}
		}
	}
</script>

